import React, { useState } from 'react';
import { useCookies } from 'react-cookie';
import "./App.css";

const App = () => {
   const [name, setName] = useState('');
   const [pwd, setPwd] = useState('');
   const [cookies, setCookie] = useCookies(['user']);

   const handle = () => {
      setCookie('Name', name, { path: '/' });
      setCookie('Password', pwd, { path: '/' });
   };
   return (
      <div className="App">
      <h1>WADS Cookie and Sessions Project</h1>
      <h4>Maria Clarin - 2501990331 - L4AC</h4>
      <img src ="https://cdn.discordapp.com/attachments/1074944157363220597/1107735500544016526/BookStack_4.png"/>
      <div className="unamefield">
         <h2 className='unametitle'>Username:</h2>
         <input
            placeholder="Enter username"
            value={name}
            onChange={(e) => setName(e.target.value)}
         />
      </div>
      <div className="passwordfield">
         <h2 className='passtitle'>Password:</h2>
         <input
            type="password"
            placeholder="Enter password"
            value={pwd}
            onChange={(e) => setPwd(e.target.value)}
         />
      </div>

      <div>
         <button onClick={handle}>Set Cookie</button>
      </div>
   </div>
   );
};
export default App;
